mapper.select:
SELECT o.*, wxuser.wxuser_name as user_name, wxuser.icon as user_icon, cmt.goods_score as goods_score,cmt.comment_content_goods, od.order_detail_id,od.order_detail_no,od.record_time,od.send_time, od.line_id,od.activity_id,od.preferential,od.goods_name, od.goods_num,od.goods_price,od.goods_id, od.goods_img, act.forecast_receive_time,od.act_goods_id, line.line_name FROM t_order AS o LEFT JOIN t_after_sale_order AS aso ON aso.`order_id` = o.`order_id` LEFT JOIN t_order_detail AS od ON od.`order_id`=o.`order_id` left join t_activity as act on od.activity_id = act.activity_id left join t_wxuser as wxuser on wxuser.wxuser_id = o.wxuser_id left join t_comment as cmt on cmt.order_no = o.order_no left join t_line as line on line.line_id = od.line_id WHERE o.delete_flag = 0 AND (o.`order_type` in (1,2,4) or aso.after_sale_type in (3,4,5) )
and o.`wxuser_id`=#{wxuserId}
and od.`line_id`=#{lineId}
and od.street_id=#{streetId}
and od.`activity_id`=#{activityId}
and o.create_time > #{startTime} and o.create_time <= #{endTime}
and o.pay_time > #{startTime} and o.pay_time <= #{endTime}
and od.record_time > #{startTime} and od.record_time <= #{endTime}
and o.pay_status in (5,6,7,9,10) and o.order_type != 11
and o.order_type != 11
and o.pay_status = #{orderStatus}

SELECT o.*, wxuser.wxuser_name as user_name, wxuser.icon as user_icon, cmt.goods_score as goods_score,cmt.comment_content_goods, od.order_detail_id,od.order_detail_no,od.record_time,od.send_time, od.line_id,od.activity_id,od.preferential,od.goods_name, od.goods_num,od.goods_price,od.goods_id, od.goods_img, act.forecast_receive_time,od.act_goods_id,od.preferential, line.line_name FROM t_order AS o LEFT JOIN t_order_detail AS od ON od.`order_id`=o.`order_id` left join t_activity as act on od.activity_id = act.activity_id left join t_wxuser as wxuser on wxuser.wxuser_id = o.wxuser_id left join t_comment as cmt on cmt.order_no = o.order_no left join t_line as line on line.line_id = od.line_id WHERE o.delete_flag = 0
and o.`wxuser_id`=#{wxuserId}
and od.`line_id`=#{lineId}
and od.street_id=#{streetId}
and od.`activity_id`=#{activityId}
and o.create_time > #{startTime} and o.create_time <= #{endTime}
and o.pay_time > #{startTime} and o.pay_time <= #{endTime}
and od.record_time > #{startTime} and od.record_time <= #{endTime}
and od.create_time > #{startTime} and o.create_time <= #{endTime}
and o.pay_status in (1,2) order by o.pay_status desc, od.send_time desc
and o.pay_status in (5,6) order by o.create_time desc
and o.pay_status in (3,4) order by o.pay_status ,od.record_time desc
and o.pay_status = #{orderStatus} order by od.record_time desc
and o.pay_status = #{orderStatus} order by o.create_time desc
order by o.create_time desc

select DISTINCT o.*, wu.wxuser_name as user_name, wu.icon as user_icon, od.order_detail_id,od.order_type,od.act_goods_id,od.goods_id,od.goods_price,od.goods_detail,od.group_leader_commission, od.preferential,od.goods_name,od.goods_img,od.update_time,od.activity_id,od.send_time,od.record_time,od.line_id, od.street_id,od.goods_num,l.line_name, cmt.goods_score as goods_score,cmt.comment_content_goods from t_order as o left join t_order_detail as od on od.order_id = o.order_id left join t_line as l on l.line_id = od.line_id left join t_comment as cmt on cmt.order_no = o.order_no left join t_wxuser as wu on wu.wxuser_id = o.wxuser_id where o.appmodel_id = #{appmodelId} and o.delete_flag = 0
and od.activity_id = #{searchOrderVo.activityId}
and o.pay_status = #{searchOrderVo.orderStatus}
and o.pay_status in(3,4)
and o.pay_status = #{searchOrderVo.orderStatus}
and o.pay_status in(5,6,7,9,10)
and od.goods_name like concat('%',#{searchOrderVo.goodsName},'%')
and wu.wxuser_name like concat('%',#{searchOrderVo.wxuserName},'%')
and o.buyer_name like concat('%',#{searchOrderVo.buyerName},'%')
and l.`line_name` like concat('%',#{searchOrderVo.lineName},'%')
and o.order_no = #{searchOrderVo.orderNo}
and o.pickup_location like concat('%',#{searchOrderVo.pickupLocation},'%')
and o.create_time > #{searchOrderVo.createOrderTimeStart}
and o.wxuser_id = #{searchOrderVo.wxuserId}
and o.create_time < #{searchOrderVo.createOrderTimeEnd}
ORDER BY pay_time DESC

SELECT SUM(pay_fee) AS volume_of_business, COUNT(order_id) AS volume_of_business_number, date(pay_time) as pay_time FROM t_order WHERE appmodel_id = #{appmodelId} AND pay_time >= #{lastWeek} AND
pay_time <= #{currentDate}
GROUP BY date(pay_time) ORDER by date(pay_time) desc

select od.activity_id,sum(o.pay_fee) as totle_fee,COUNT(o.order_id) as toole_sum,od.wxuser_id from t_activity act left join t_order_detail od on od.activity_id = act.activity_id left join t_order o on o.order_id = od.order_id where act.appmodel_id = #{appmodelId} and od.appmodel_id = act.appmodel_id and od.activity_id in
#{id}
and o.pay_status in(1,2,3,4) group by od.activity_id,od.wxuser_id

select o.order_id,o.wxuser_id,o.order_no,o.order_type,o.pay_status,o.buyer_name,o.buyer_phone,o.buyer_address,o.pickup_location,o.total_fee, o.discounts_fee,o.pay_fee, o.user_desc,o.shop_desc,o.pay_channel,o.create_time,o.pay_time, o.group_id,o.group_leader_phone,o.group_leader_name,o.group_leader_icon,o.postil,o.appmodel_id, w.wxuser_Name,w.icon, c.goods_score, c.group_score, c.comment_content_goods,c.comment_content_group, od.goods_name, od.goods_id, od.goods_img, od.goods_price, od.preferential,od.goods_num,od.update_time,od.send_time,od.record_time, o.after_sale_order_id,o.user_after_sale_num, l.line_name, sb.send_bill_id,sb.send_bill_name,sb.create_date,sb.status,ag.activity_price from t_order as o left join t_order_detail as od on o.order_id = od.order_id left join t_line as l on l.line_id = od.line_id left join t_comment as c on c.order_no = o.order_no left join t_wxuser as w on w.wxuser_id =o.wxuser_id left join t_order_send_bill_mapping as om on om.order_id = o.order_id left join t_send_bill as sb on sb.send_bill_id = om.send_bill_id left join t_activity_goods as ag on ag.activity_goods_id = od.act_goods_id where o.delete_flag = 0 and o.appmodel_id = #{appmodelId}
and od.goods_name like concat('%',#{searchOrderVo.goodsName},'%')
and w.wxuser_name like concat('%',#{searchOrderVo.wxuserName},'%')
and o.buyer_name like concat('%',#{searchOrderVo.buyerName},'%')
and o.order_no = #{searchOrderVo.orderNo}
and o.order_id = #{searchOrderVo.orderId}
and o.pickup_location like concat('%',#{searchOrderVo.pickupLocation},'%')
and o.group_leader_name like concat('%',#{searchOrderVo.groupName},'%')
and od.line_id = #{searchOrderVo.lineId}
AND o.pay_status = 4 AND c.del_flag =0
AND (o.pay_status = 4 or o.pay_status = 3)
and o.pay_status in (5,6,7,9,10)
and o.pay_status = #{searchOrderVo.orderStatus}
and o.create_time > #{startDate} and o.create_time <= #{endDate}
and om.generate = 0 and om.del_flag = 0
and om.send_bill_id = #{searchOrderVo.sendBillId} and om.del_flag = 0 and om.generate = 1

select * from t_order as o
left join t_order_detail as od on o.order_id = od.order_id
where o.wxuser_id = #{wxuserId}
and o.group_id = #{groupLeaderId}
and o.pay_status=#{status}
